from typing import List


class Solution:
    def minimumTotal(self, triangle: List[List[int]]) -> int:
        dp = [0] * len(triangle[-1])

        for lvl in range(len(triangle)):
            dp_copy = dp.copy()

            for i in range(lvl + 1):
                if i == 0:
                    v = dp_copy[0]
                elif i == lvl:
                    v = dp_copy[lvl - 1]
                else:
                    v = min(dp_copy[i - 1], dp_copy[i])
                dp[i] = v + triangle[lvl][i]

        return min(dp)



