from typing import List


class Solution:
    def minimumTotal(self, triangle: List[List[int]]) -> int:

        if len(triangle) == 1:
            return triangle[0][0]

        return self.minimum_total_rec(triangle[1:], triangle[0][0], 0)

    def minimum_total_rec(self, triangle: List[List[int]], acc: int, parent_ix: int) -> int:
        if not triangle:
            return acc

        t = triangle[0]
        best = float('inf')
        for ix in [parent_ix, parent_ix + 1]:
            best = min(best, self.minimum_total_rec(triangle[1:], acc + t[ix], ix))

        return best


